// client/src/components/Home.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Home() {
  const [items, setItems] = useState([]);
  const [category, setCategory] = useState('');

  useEffect(() => {
    fetchItems();
  }, [category]);

  const fetchItems = async () => {
    try {
      const url = category ? `/api/items?category=${category}` : '/server/models/item.js';
      const response = await axios.get(url);
      setItems(response.data);
    } catch (error) {
      console.error("Error fetching items:", error);
    }
  };

  return (
    <div>
      <h1>E-Commerce Home</h1>
      <div>
        <label>Filter by Category: </label>
        <select value={category} onChange={e => setCategory(e.target.value)}>
          <option value="">All</option>
          <option value="Vinyls">Vinyls</option>
          <option value="Antique Furniture">Antique Furniture</option>
          <option value="GPS SportWatches">GPS SportWatches</option>
          <option value="Running Shoes">Running Shoes</option>
        </select>
      </div>
      <div>
        {items.map(item => (
          <div key={item._id} style={{border: '1px solid #ccc', margin: '10px', padding: '10px'}}>
            <h3>{item.name}</h3>
            <img src={item.image} alt={item.name} style={{width: '100px'}}/>
            <p>{item.description}</p>
            <p>Price: ${item.price}</p>
            <p>Rating: {item.rating}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
