// client/src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [isRegistering, setIsRegistering] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isRegistering) {
      // Registration flow: send a POST request to create a new user.
      try {
        const response = await axios.post('/api/users', { username, password });
        console.log("User registered:", response.data);
        alert("Registration successful. You can now log in.");
        setIsRegistering(false);
        setUsername('');
        setPassword('');
      } catch (error) {
        console.error("Registration error:", error);
        alert("Registration failed. Please try again.");
      }
    } else {
      // Login flow: fetch user by username and validate credentials.
      try {
        const response = await axios.get(`/api/users/${username}`);
        const user = response.data;
        if (user && user.password === password) {
          localStorage.setItem('user', JSON.stringify(user));
          navigate('/');
        } else {
          alert("Invalid credentials");
        }
      } catch (error) {
        console.error("Login error:", error);
        alert("Login failed");
      }
    }
  };

  return (
    <div>
      <h1>{isRegistering ? "Register" : "Login"}</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text"
          placeholder="Username" 
          value={username} 
          onChange={e => setUsername(e.target.value)} 
          required 
        />
        <input 
          type="password"
          placeholder="Password" 
          value={password} 
          onChange={e => setPassword(e.target.value)} 
          required 
        />
        <button type="submit">{isRegistering ? "Register" : "Login"}</button>
      </form>
      <p style={{cursor: "pointer", textDecoration: "underline"}} onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? "Already have an account? Login here." : "Don't have an account? Register here."}
      </p>
    </div>
  );
}

export default Login;
