// components/AdminDashboard.js
import React, { useState } from 'react';

const AdminDashboard = () => {
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    price: '',
    category: 'Vinyls',
    image: ''
  });

  const [newUser, setNewUser] = useState({
    username: '',
    password: '',
    isAdmin: false
  });

  const handleAddItem = (e) => {
    e.preventDefault();
    // Add item logic (e.g., API call to backend)
    console.log('Adding item: ', newItem);
    setNewItem({
      name: '',
      description: '',
      price: '',
      category: 'Vinyls',
      image: ''
    });
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    // Add user logic (e.g., API call to backend)
    console.log('Adding user: ', newUser);
    setNewUser({
      username: '',
      password: '',
      isAdmin: false
    });
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <section>
        <h3>Add New Item</h3>
        <form onSubmit={handleAddItem}>
          <input
            type="text"
            placeholder="Item Name"
            value={newItem.name}
            onChange={(e) => setNewItem({...newItem, name: e.target.value})}
            required
          />
          <textarea
            placeholder="Description"
            value={newItem.description}
            onChange={(e) => setNewItem({...newItem, description: e.target.value})}
            required
          />
          <input
            type="number"
            placeholder="Price"
            value={newItem.price}
            onChange={(e) => setNewItem({...newItem, price: e.target.value})}
            required
          />
          <select
            value={newItem.category}
            onChange={(e) => setNewItem({...newItem, category: e.target.value})}
          >
            <option value="Vinyls">Vinyls</option>
            <option value="Antique Furniture">Antique Furniture</option>
            <option value="GPS SportWatches">GPS SportWatches</option>
            <option value="Running Shoes">Running Shoes</option>
          </select>
          <input
            type="text"
            placeholder="Image URL"
            value={newItem.image}
            onChange={(e) => setNewItem({...newItem, image: e.target.value})}
          />
          <button type="submit">Add Item</button>
        </form>
      </section>
      <section>
        <h3>Add New User</h3>
        <form onSubmit={handleAddUser}>
          <input
            type="text"
            placeholder="Username"
            value={newUser.username}
            onChange={(e) => setNewUser({...newUser, username: e.target.value})}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={newUser.password}
            onChange={(e) => setNewUser({...newUser, password: e.target.value})}
            required
          />
          <label>
            <input
              type="checkbox"
              checked={newUser.isAdmin}
              onChange={(e) => setNewUser({...newUser, isAdmin: e.target.checked})}
            />
            Admin Privileges
          </label>
          <button type="submit">Add User</button>
        </form>
      </section>
      {/* Additional functionalities (Remove Item/User) can be added similarly */}
    </div>
  );
};

export default AdminDashboard;
