// client/src/components/UserProfile.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Get the logged-in user from localStorage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser && storedUser.username) {
      // Fetch full details from the backend using the username
      axios.get(`/api/users/${storedUser.username}`)
        .then(res => setUser(res.data))
        .catch(err => console.error("Error fetching user profile:", err));
    }
  }, []);

  if (!user) {
    return <div className="container panel"><p>Loading...</p></div>;
  }

  return (
    <div className="container panel">
      <h1>{user.username}&apos;s Profile</h1>
      <p>
        <strong>Average Review Rating:</strong> {user.averageRating || 'No ratings yet'}
      </p>
      <h2>Reviews</h2>
      {user.reviews && user.reviews.length > 0 ? (
        <ul>
          {user.reviews.map((review, index) => (
            <li key={index}>
              <strong>Item ID:</strong> {review.itemId} <br />
              <strong>Review:</strong> {review.text}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reviews written yet.</p>
      )}
    </div>
  );
}

export default UserProfile;
