// components/HomePage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const itemsData = [
  {
    id: 1,
    name: 'Vinyl Record A',
    category: 'Vinyls',
    description: 'A classic vinyl record.',
    price: 20,
    image: 'https://example.com/vinyl1.jpg'
  },
  {
    id: 2,
    name: 'Antique Chair',
    category: 'Antique Furniture',
    description: 'A beautiful antique chair.',
    price: 150,
    image: 'https://example.com/chair.jpg'
  },
  {
    id: 3,
    name: 'GPS Sport Watch X',
    category: 'GPS SportWatches',
    description: 'A modern GPS watch.',
    price: 100,
    image: 'https://example.com/watch.jpg'
  },
  {
    id: 4,
    name: 'Running Shoes Pro',
    category: 'Running Shoes',
    description: 'High quality running shoes.',
    price: 80,
    image: 'https://example.com/shoes.jpg'
  },
  // add more items as needed
];

const categories = ['All', 'Vinyls', 'Antique Furniture', 'GPS SportWatches', 'Running Shoes'];

const HomePage = ({ user }) => {
  const [filter, setFilter] = useState('All');

  const filteredItems = filter === 'All' ? itemsData : itemsData.filter(item => item.category === filter);

  return (
    <div>
      <h2>Home Page</h2>
      <div>
        <label>Filter by category: </label>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>
      <div className="item-list">
        {filteredItems.map(item => (
          <div key={item.id} className="item-card">
            <img src={item.image} alt={item.name} width="150" />
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            <p>${item.price}</p>
            <Link to={`/item/${item.id}`}>View Details</Link>
          </div>
        ))}
      </div>
      {!user && (
        <div>
          <p>Please <a href="/login">login</a> to rate or review items.</p>
        </div>
      )}
    </div>
  );
};

export default HomePage;
